import React from 'react';
import SearchBar from './SearchBar';
import youtube from '../api/youtube';
import VideoList from '../components/VideoList';
import VideoDetails from '../components/VideoDetail';

class App extends React.Component {
  state = { videos: [], selectedVideo: null };

  //Set default Video when the page mount
  componentDidMount = () => {
    this.onTextSubmit('Cong');
  };

  onTextSubmit = async (text) => {
    const response = await youtube.get('/search', {
      params: {
        q: text,
      },
    });

    this.setState({
      videos: response.data.items,
      selectedVideo: response.data.items[0],
    });
  };

  onVideoSelect = (video) => {
    console.log('From the App!', video);
    this.setState({ selectedVideo: video });
  };

  render() {
    return (
      <div className="ui container" style={{ marginTop: '3%' }}>
        <SearchBar onFormSubmit={this.onTextSubmit} />
        <div className="ui grid">
          <div className="ui row">
            <div className="ten wide column centered">
              <div className="ui segment">
                <div className="ui active dimmer">
                  <div className="ui medium text loader">Select Video ...</div>
                </div>
              </div>
              {/* <VideoDetails video={this.state.selectedVideo} /> */}
            </div>
            <div className="six wide column">
              <VideoList
                videos={this.state.videos}
                onVideoSelect={this.onVideoSelect}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
